import React, { Component } from "react";
import { Modal, Button, Row, Col, Form } from "react-bootstrap";

class UpdateUser extends Component {
  constructor(props) {
    super(props);
    this.state = { users: [] };
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleSubmit(event) {
    console.log(this.state);
    event.preventDefault();
    fetch("http://localhost:8080/users", {
      method: "PUT",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        id: event.traget.id.value,
        name: event.target.name.value,
        surname: event.target.surname.value,
        gender: event.target.gender.value,
        birthdate: event.target.birthdate.value,
        work_address: event.target.work_address.value,
        home_address: event.target.home_address.value,
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          //alert(result);
          this.setState({ user_added: true });
        },
        (error) => {
          //alert('Failed')
          this.setState({ user_added: true });
        }
      );
    event.target.reset();
    console.log(this.sts_msg);
  }

  render() {
    const groupstyle = { padding: "5px 2px" };
    return (
      <div className="container">
        <Modal
          {...this.props}
          size="lg"
          aria-labelledby="contained-modal-title-vcenter"
          centered
        >
          <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
              Update User
            </Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <Row>
              <Col sm={6}>
                <Form onSubmit={this.handleSubmit}>
                  <Form.Group controlId="userId" style={groupstyle}>
                    <Form.Label>User ID</Form.Label>
                    <Form.Control
                      type="text"
                      name="userId"
                      disabled
                      defaultValue={this.props.userId}
                      placeholder="userId"
                    />
                  </Form.Group>

                  <Form.Group controlId="userName" style={groupstyle}>
                    <Form.Label>Name</Form.Label>
                    <Form.Control
                      type="text"
                      name="userName"
                      required
                      defaultValue={this.props.userName}
                      placeholder="New Name"
                    />
                  </Form.Group>

                  <Form.Group controlId="userSurname" style={groupstyle}>
                    <Form.Label>Surname</Form.Label>
                    <Form.Control
                      type="text"
                      name="userSurname"
                      required
                      defaultValue={this.props.userSurname}
                      placeholder="New Surname"
                    />
                  </Form.Group>

                  <Form.Group controlId="userGender" style={groupstyle}>
                    <Form.Label>Gender</Form.Label>
                    <Form.Control
                      as="select"
                      name="userGender"
                      required
                      defaultValue={this.props.userGender}
                    >
                      <option>M</option>
                      <option>F</option>
                    </Form.Control>
                  </Form.Group>

                  <Form.Group controlId="userBirthdate" style={groupstyle}>
                    <Form.Label>Birthdate</Form.Label>
                    <Form.Control
                      type="date"
                      name="userBirthdate"
                      required
                      defaultValue={this.props.userBirthdate}
                      placeholder="userBirthdate"
                    />
                  </Form.Group>

                  <Form.Group controlId="userWorkAddress" style={groupstyle}>
                    <Form.Label>Work Address</Form.Label>
                    <Form.Control
                      type="text"
                      name="userWorkAddress"
                      required
                      defaultValue={this.props.userWorkAddress}
                      placeholder="New Work Address"
                    />
                  </Form.Group>

                  <Form.Group controlId="userHomeAddress" style={groupstyle}>
                    <Form.Label>Home Address</Form.Label>
                    <Form.Control
                      type="text"
                      name="userHomeAddress"
                      required
                      defaultValue={this.props.userHomeAddress}
                      placeholder="New Home Address"
                    />
                  </Form.Group>

                  <Form.Group>
                    <Button variant="primary" type="submit" onClick={this.handleUpdate}>
                      Update User
                    </Button>
                  </Form.Group>
                </Form>
              </Col>
            </Row>
          </Modal.Body>
        </Modal>
      </div>
    );
  }
}

export default UpdateUser;
