import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import { Button, Form, Row, Col } from "react-bootstrap";

class RegisterUserComponent2 extends Component {
  state = {};

  handleSubmit(event){
    console.log(event.target.DOJ.value);
  event.preventDefault();
  fetch('http://localhost:49902/api/employee',{
    method:'POST',
    headers:{
      'Accept':'application/json',
      'Content-Type':'application/json'
    },
    body:JSON.stringify({
      EmployeeID:null,
      EmployeeName: event.target.EmployeeName.value,
      Department: event.target.Department.value,
      MailID: event.target.MailID.value,
      DOJ: event.target.DOJ.value

    })
  })
  .then(res=> res.json())
  .then((result)=>
  {
      //alert(result);
      this.setState({snackbaropen:true, snackbarmsg:result});
  },
  (error)=>{
    //alert('Failed')
    this.setState({snackbaropen:true, snackbarmsg:'failed'});
  }
  )
}
  render() {

    const groupstyle = {padding: "5px 2px"};
    return (
      <div className="container">
        <div className="row">
          <div className="card" style={{ padding: "10px 0" }}>
            <h3 className="text-center">Add User Form</h3>
            <div className="card-body">
              <Form onSubmit={this.handleSubmit}>

                <Form.Group controlId="Name" style={groupstyle}>
                  <Form.Label>Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="Name"
                    required
                    placeholder="Name"
                  />
                </Form.Group>

                <Form.Group controlId="Surname" style={groupstyle}>
                  <Form.Label>Surname</Form.Label>
                  <Form.Control
                    type="text"
                    name="Surname"
                    required
                    placeholder="Surname"
                  />
                </Form.Group>

                <Form.Group controlId="Gender" style={groupstyle}>
                  <Form.Label>Gender</Form.Label>
                  <Form.Control as="select">
                    <option>M</option>
                    <option>F</option>
                  </Form.Control>
                </Form.Group>

                <Form.Group controlId="Birthdate" style={groupstyle}>
                  <Form.Label>Birthdate</Form.Label>
                  <Form.Control
                    type="date"
                    name="Birthdate"
                    required
                    placeholder="Birthdate"
                  />
                </Form.Group>

                <Form.Group controlId="Work Address" style={groupstyle}>
                  <Form.Label>Work Address</Form.Label>
                  <Form.Control
                    type="text"
                    name="Work Address"
                    required
                    placeholder="Work Address"
                  />
                </Form.Group>

                <Form.Group controlId="Home Address" style={groupstyle}>
                  <Form.Label>Home Address</Form.Label>
                  <Form.Control
                    type="text"
                    name="Home Address"
                    required
                    placeholder="Home Address"
                  />
                </Form.Group>

                <Form.Group style={{ paddingTop: "20px" }}>
                  <Button variant="primary" type="submit">
                    Add User
                  </Button>
                </Form.Group>
              </Form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default RegisterUserComponent2;
