 import React, { Component } from 'react';
 import {Modal, Button, Row, Col, Form} from 'react-bootstrap';

class UpdateUser extends Component {
    state = {  } 

    handleSubmit(event){
        console.log(this.state);
      event.preventDefault();
      fetch('http://localhost:8080/users',{
        method:'PUT',
        headers:{
          'Accept':'application/json',
          'Content-Type':'application/json'
        },
        body:JSON.stringify({
          id: event.traget.id.value,
          name: event.target.name.value,
          surname: event.target.surname.value,
          gender: event.target.gender.value,
          birthdate: event.target.birthdate.value,
          work_address: event.target.work_address.value,
          home_address: event.target.home_address.value,

        })
      })
      .then(res=> res.json())
      .then((result)=>
      {
          //alert(result);
          this.setState({user_added:true});
      },
      (error)=>{
        //alert('Failed')
        this.setState({user_added:true});
      }
      );
      event.target.reset();
      console.log(this.sts_msg);
    }

    render() { 
        return (
            <div className='container'>
                <Modal
                    size="lg"
                    aria-labelledby="contained-modal-title-vcenter"
                    centered>
                <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Edit Employee
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Row>
              <Col sm={6}>
              <Form onSubmit={this.handleSubmit}>


              <Form.Group controlId="EmployeeID">
              <Form.Label>EmployeeID</Form.Label>
              <Form.Control
                type="text"
                name="EmployeeID"
                disabled
                defaultValue={this.props.empid}
                placeholder="EmployeeID"
               />
              </Form.Group>

              <Form.Group controlId="EmployeeName">
              <Form.Label>EmployeeName</Form.Label>
              <Form.Control
                type="text"
                name="EmployeeName"
                required
                defaultValue={this.props.empname}
                placeholder="EmployeeName"
               />
              </Form.Group>

              <Form.Group controlId="Department">
              <Form.Label>Department</Form.Label>

             <Form.Control as="select" defaultValue={this.props.depmt}>
                {this.state.deps.map(dep =>
                <option key={dep.DepartmentID}>{dep.DepartmentName}</option>
                    )}
             </Form.Control>

              </Form.Group>

              <Form.Group controlId="MailID">
              <Form.Label>MailID</Form.Label>
              <Form.Control
                type="text"
                name="MailID"
                required
                defaultValue={this.props.mailid}
                placeholder="MailID"
               />
              </Form.Group>

              <Form.Group controlId="DOJ">
              <Form.Label>DOJ</Form.Label>
              <Form.Control
                type="date"
                name="DOJ"
                required
                defaultValue={this.props.doj}
                placeholder="DOJ"
               />
              </Form.Group>

              <Form.Group>
                  <Button variant="primary" type="submit">
                  Update Employee
                  </Button>
              </Form.Group>
              </Form>
              </Col>
          </Row>


        </Modal.Body>
        <Modal.Footer>
          <Button variant="danger" onClick={this.props.onHide}>Close</Button>
        </Modal.Footer>
                </Modal>
            </div>
        );
    }
}

export default UpdateUser;
