import React, { Component } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import { Button, Form, Row, Col } from "react-bootstrap";

class RegisterUserComponent2 extends Component {
    constructor(props){
        super(props);
        this.state = {deps:[], snackbaropen: false, snackbarmsg: ''};
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount(){
        fetch('http://localhost:49902/api/department')
        .then(response => response.json())
        .then(data => {
        this.setState({deps:data});
        });
    }

    snackbarClose = (event) =>{
        this.setState({snackbaropen:false});
      };

  handleSubmit(event){
    console.log(event.target.DOJ.value);
  event.preventDefault();
  fetch('http://localhost:8080/users',{
    method:'POST',
    headers:{
      'Accept':'application/json',
      'Content-Type':'application/json'
    },
    body:JSON.stringify({
      name: event.target.name.value,
      surname: event.target.surname.value,
      gender: event.target.gender.value,
      birthdate: event.target.birthdate.value,
      work_address: event.target.work_address.value,
      home_address: event.target.home_address.value,

    })
  })
  .then(res=> res.json())
  .then((result)=>
  {
      //alert(result);
      this.setState({snackbaropen:true, snackbarmsg:result});
  },
  (error)=>{
    //alert('Failed')
    this.setState({snackbaropen:true, snackbarmsg:'failed'});
  }
  )
}
  render() {

    const groupstyle = {padding: "5px 2px"};
    return (
      <div className="container">
        <div className="row">
          <div className="card" style={{ padding: "10px 0" }}>
            <h3 className="text-center">Add User Form</h3>
            <div className="card-body">
              <Form onSubmit={this.handleSubmit}>

                <Form.Group controlId="name" style={groupstyle}>
                  <Form.Label>Name</Form.Label>
                  <Form.Control
                    type="text"
                    name="name"
                    required
                    placeholder="Name"
                  />
                </Form.Group>

                <Form.Group controlId="surname" style={groupstyle}>
                  <Form.Label>Surname</Form.Label>
                  <Form.Control
                    type="text"
                    name="surname"
                    required
                    placeholder="Surname"
                  />
                </Form.Group>

                <Form.Group controlId="gender" style={groupstyle}>
                  <Form.Label>Gender</Form.Label>
                  <Form.Control as="select">
                    <option>M</option>
                    <option>F</option>
                  </Form.Control>
                </Form.Group>

                <Form.Group controlId="birthdate" style={groupstyle}>
                  <Form.Label>Birthdate</Form.Label>
                  <Form.Control
                    type="date"
                    name="birthdate"
                    required
                    placeholder="Birthdate"
                  />
                </Form.Group>

                <Form.Group controlId="work_address" style={groupstyle}>
                  <Form.Label>Work Address</Form.Label>
                  <Form.Control
                    type="text"
                    name="work_address"
                    required
                    placeholder="Work Address"
                  />
                </Form.Group>

                <Form.Group controlId="home_address" style={groupstyle}>
                  <Form.Label>Home Address</Form.Label>
                  <Form.Control
                    type="text"
                    name="home_address"
                    required
                    placeholder="Home Address"
                  />
                </Form.Group>

                <Form.Group style={{ paddingTop: "20px" }}>
                  <Button variant="primary" type="submit">
                    Add User
                  </Button>
                </Form.Group>
              </Form>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default RegisterUserComponent2;
