import React, { Component } from 'react';
import UpdateUser from './UpdateUser';
import { Button} from "react-bootstrap";

class ListUsersComponent extends Component {

    // Constructor
	constructor(props) {
		super(props);

		this.state = {
			users: [],
			DataisLoaded: false
		};
	}

    // ComponentDidMount is used to
	// execute the code
	componentDidMount() {
		this.userslist();
	}

    componentDidUpdate(){
        this.userslist();
    }

    userslist(){
        fetch("http://localhost:8080/users", {method:"GET"})
		.then((res) => res.json())
		.then((json) => {
				this.setState({
					users: json,
					DataisLoaded: true
				});
			})
        }

    deleteUser(userId)
    {
        if(window.confirm('Are you sure?'))
        {
            fetch('http://localhost:8080/users/'+userId,{
                method:'DELETE',
                header:{'Accept':'application/json',
                'Content-Type':'application/json'
            }
            })
        }
    }

    updateUser(userId)
    {
        fetch("http://localhost:8080/users/"+userId)
		.then((res) => res.json())
		.then((data) => {
				console.log(data);
			})

        console.log(userId);

    }

    render() { 

        const { DataisLoaded, users } = this.state;
		if (!DataisLoaded) return <div>
			<h1> Pleses wait some time.... </h1> </div> ;

		return (
            <div className="container">
        <div className="row">
          <div className="card" style={{ padding: "10px 0" }}>
            <h3 className="text-center">Users List</h3>
            <div className="card-body">
            {/* <div>
            <h2 className='text-center'>Users List</h2>
            <div className='row'> */}
                <table className='table table-striped table-bordered table-responsive'>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Surname</th>
                            <th>Gender</th>
                            <th>Birthdate</th>
                            <th>Work Address</th>
                            <th>Home Address</th>
                            <th>Actions</th>
                        </tr>
                    </thead>

                    <tbody>
                        {
                            this.state.users.map( user =>
                                <tr key = {user.id}>
                                    <td>{user.name}</td>
                                    <td>{user.surname}</td>
                                    <td>{user.gender}</td>
                                    <td>{user.birthdate}</td>
                                    <td>{user.work_address}</td>
                                    <td>{user.home_address}</td>
                                    <td>
                                    {/* <button type="button" className="btn btn-outline-primary btn-sm"
                                        style={{"marginRight":"10px"}} onClick={()=> this.updateUser(user.id)}>Update</button> */}
                                    <button type="button" className="btn btn-outline-danger btn-sm"
                                        onClick={()=> this.deleteUser(user.id)}>Delete</button>

                                    <Button
className="mr-2" variant="info"
onClick= {()=> this.setState({editModalShow:true, 
userId:user.id, userName:user.name, 
userSurname:user.surname, userGender:user.gender,
userBirthdate:user.birthdate, userWorkAddress: user.work_address, userHomeAddress: user.home_address})}
>
    Edit
</Button>
                                    <UpdateUser
show = {this.state.editModalShow}
// onHide={editModalClose}
userId = {userId}
userName = {userName}
userSurname={userSurname}
userGender={userGender}
userBirthdate={userBirthdate}
userWorkAddress={userWorkAddress}
userHomeAddress={userHomeAddress}
/>
                                    </td>
                                </tr>     
                            )

                        }

                    </tbody>
                </table>
            </div>
            </div></div></div>
        //     </div>
        // </div>
	);

    }
}

export default ListUsersComponent;
